/** *  * @author Brandon Mathew. * * The priority node class. */public class PriorityNode<E>{		private PriorityNode next = null;		private E element = null;		private double priority;		public PriorityNode(){		next = null;		element = null;		priority = Double.MAX_VALUE;		}				public PriorityNode(E elem) {			next = null;			element = elem;					}	public PriorityNode(E elem, double my_priority) {		next = null;		element = elem;		priority = my_priority;	}		public PriorityNode getNext() {			return next;		}		public E getElement() {			return element;		}		public double getPriority() {			return priority;		}		public void setNext(PriorityNode next) {			this.next = next;		}		public void setElement(E element) {			this.element = element;		}		public void setPriority(double priority) {			this.priority = priority;		}	}